var r=new fabric.Canvas("fabric-canvas");document.getElementById("imageInput").addEventListener("change",function(n){var a=n.target.files;if(a.length)for(var e=0;e<a.length;e++){var s=a[e],i=new FileReader;i.onload=function(l){var t=new Image;t.src=l.target.result,t.onload=function(){var d=new fabric.Image(t);r.add(d)}},i.readAsDataURL(s)}});document.getElementById("resizeButton").addEventListener("click",function(){var n=parseInt(prompt("Enter new width:"),10),a=parseInt(prompt("Enter new height:"),10);if(!isNaN(n)&&!isNaN(a)){var e=r.getActiveObject();e?(e.scaleToWidth(n),e.scaleToHeight(a),r.renderAll()):alert("Please select an image to resize.")}else alert("Please enter valid dimensions.")});
